package com.example.chatbot.chat.dto;import lombok.AccessLevel;import lombok.Getter;import lombok.NoArgsConstructor;/** * 채팅 응답 DTO * * @author : 오승훈 * @fileName : ChatResponse * @since : 2026-02-05 */@Getter@NoArgsConstructor(access = AccessLevel.PROTECTED)public class ChatResponse {  private String answer;  public ChatResponse(String answer) {    this.answer = answer;  }  public static ChatResponse of(String answer) {    return new ChatResponse(answer);  }}