package com.example.chatbot.chat.dto;import com.example.chatbot.chat.entity.Conversation;import java.time.LocalDateTime;import lombok.AllArgsConstructor;import lombok.Getter;/** * 대화 목록 반환 DTO * * @author : 오승훈 * @fileName : ConversationListResponse * @since : 2026-02-09 */@Getter@AllArgsConstructorpublic class ConversationListResponse {  private Long conversationId;  private String title;  private LocalDateTime createdAt;  private LocalDateTime updatedAt;  public static ConversationListResponse from(Conversation conversation) {    return new ConversationListResponse(        conversation.getConversationId(),        conversation.getTitle(),        conversation.getCreatedAt(),        conversation.getUpdatedAt()    );  }}