package com.example.chatbot.chat.entity;import com.example.chatbot.common.entity.BaseTime;import com.example.chatbot.user.entity.User;import jakarta.persistence.*;import lombok.AccessLevel;import lombok.Getter;import lombok.NoArgsConstructor;/** * conversation 엔티티 * * @author : 오승훈 * @fileName : Conversation * @since : 2026-02-04 */@Entity@Table(name = "conversation")@Getter@NoArgsConstructor(access = AccessLevel.PROTECTED)public class Conversation extends BaseTime {  @Id  @GeneratedValue(strategy = GenerationType.IDENTITY)  @Column(name = "conversation_id")  private Long conversationId;  @Column(name = "title")  private String title;  @ManyToOne(fetch = FetchType.LAZY)  @JoinColumn(name = "user_id", nullable = false)  private User user;}