package com.example.chatbot.common.config;import com.example.chatbot.common.interceptor.ApiKeyInterceptor;import lombok.RequiredArgsConstructor;import org.springframework.context.annotation.Configuration;import org.springframework.web.servlet.config.annotation.InterceptorRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;/** * WebConfig * * @author : 오승훈 * @fileName : WebConfig * @since : 2026-02-19 */@Configuration@RequiredArgsConstructorpublic class WebConfig implements WebMvcConfigurer {  private final ApiKeyInterceptor apiKeyInterceptor;  @Override  public void addInterceptors(InterceptorRegistry registry) {    registry.addInterceptor(apiKeyInterceptor)        .addPathPatterns("/api/**")        .excludePathPatterns(            "/api/auth/**",            "/swagger-ui/**",            "/v3/api-docs/**",            "error"        );  }}