package com.example.chatbot.common.entity;import jakarta.persistence.Column;import jakarta.persistence.EntityListeners;import jakarta.persistence.MappedSuperclass;import java.time.LocalDateTime;import lombok.Getter;import org.springframework.data.annotation.CreatedDate;import org.springframework.data.annotation.LastModifiedDate;import org.springframework.data.jpa.domain.support.AuditingEntityListener;/** * 공통 타임스탬프 관련 엔티티입니다 * * @author : 오승훈 * @fileName : BaseTime * @since : 2026-02-03 */@Getter@MappedSuperclass@EntityListeners(AuditingEntityListener.class)public abstract class BaseTime {  @CreatedDate  @Column(name = "created_at",updatable = false)  private LocalDateTime createdAt;  @LastModifiedDate  @Column(name = "updated_at")  private LocalDateTime updatedAt;}