package com.example.chatbot.common.exception;import lombok.Getter;/** * * 비즈니스 예외 기본 클래스  * @fileName        : BusinessException * @author          : 오승훈 * @since           : 2026-02-12 *  */@Getterpublic class BusinessException extends RuntimeException {  private final ErrorCode errorCode;  public BusinessException(ErrorCode errorCode) {    super(errorCode.getMessage());    this.errorCode = errorCode;  }  public BusinessException(String message, ErrorCode errorCode) {    super(message);    this.errorCode = errorCode;  }}