package com.example.chatbot.common.exception;/** * 대화 접근 불가 예외 * * @author : 오승훈 * @fileName : ConversationAccessDeniedException * @since : 2026-02-22 */public class ConversationAccessDeniedException extends ForbiddenException {  public ConversationAccessDeniedException() {    super(ErrorCode.CONVERSATION_ACCESS_DENIED);  }  public ConversationAccessDeniedException(Long conversationId) {    super("대화 ID " + conversationId + "에 접근할 수 없습니다.",        ErrorCode.CONVERSATION_ACCESS_DENIED);  }}