package com.example.chatbot.common.exception;/** * 중복되는 로그인 id 입력 * * @author : 오승훈 * @fileName : DuplicateLoginIdException * @since : 2026-02-22 */public class DuplicateLoginIdException extends BusinessException {  public DuplicateLoginIdException() {    super(ErrorCode.DUPLICATE_LOGIN_ID);  }  public DuplicateLoginIdException(String loginId) {    super(loginId + "은(는) 이미 사용 중인 로그인 ID입니다.", ErrorCode.DUPLICATE_LOGIN_ID);  }}