package com.example.chatbot.message.dto;import com.example.chatbot.message.entity.Message;import com.example.chatbot.message.entity.MessageRole;import java.time.LocalDateTime;import lombok.AllArgsConstructor;import lombok.Getter;/** * 메시지 응답 DTO * * @author : 오승훈 * @fileName : MessageResponse * @since : 2026-02-09 */@Getter@AllArgsConstructorpublic class MessageResponse {  private Long messageId;  private MessageRole role;  private String content;  private LocalDateTime createdAt;  public static MessageResponse from(Message message) {    return new MessageResponse(        message.getMessageId(),        message.getRole(),        message.getContent(),        message.getCreatedAt()    );  }}