package com.example.chatbot.message.entity;import com.example.chatbot.common.entity.BaseTime;import com.example.chatbot.chat.entity.Conversation;import jakarta.persistence.*;import lombok.AccessLevel;import lombok.Getter;import lombok.NoArgsConstructor;/** * message 엔티티 * * @author : 오승훈 * @fileName : Message * @since : 2026-02-04 */@Entity@Table(name = "message")@Getter@NoArgsConstructor(access = AccessLevel.PROTECTED)public class Message extends BaseTime {  @Id  @GeneratedValue(strategy = GenerationType.IDENTITY)  @Column(name = "message_id")  private Long messageId;  @Enumerated(EnumType.STRING)  @Column(name = "role", nullable = false, length = 20)  private MessageRole role;  @Column(name = "content", nullable = false, columnDefinition = "TEXT")  private String content;  @ManyToOne(fetch = FetchType.LAZY)  @JoinColumn(name = "conversation_id", nullable = false)  private Conversation conversation;}