package com.example.chatbot.user.controller;import com.example.chatbot.user.dto.LoginRequest;import com.example.chatbot.user.dto.LoginResponse;import com.example.chatbot.user.dto.SignupRequest;import com.example.chatbot.user.dto.SignupResponse;import com.example.chatbot.user.service.AuthService;import jakarta.validation.Valid;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * auth 컨트롤러 * * @author : 오승훈 * @fileName : AuthController * @since : 2026-02-22 */@Slf4j@RestController@RequestMapping("/api/auth")@RequiredArgsConstructorpublic class AuthController {  private final AuthService authService;  /**   * 회원가입   */  @PostMapping("/signup")  public ResponseEntity<SignupResponse> signup(@Valid @RequestBody SignupRequest request) {    log.info("회원가입 요청 수신 - 로그인 ID: {}", request.getLoginId());    SignupResponse response = authService.signup(request);    return ResponseEntity.ok(response);  }  /**   * 로그인   */  @PostMapping("/login")  public ResponseEntity<LoginResponse> login(@Valid @RequestBody LoginRequest request) {    log.info("로그인 요청 수신 - 로그인 ID: {}", request.getLoginId());    LoginResponse response = authService.login(request);    return ResponseEntity.ok(response);  }}