package com.example.chatbot.user.controller;import com.example.chatbot.user.dto.LoginRequest;import com.example.chatbot.user.dto.LoginResponse;import com.example.chatbot.user.dto.SignupRequest;import com.example.chatbot.user.dto.SignupResponse;import com.example.chatbot.user.service.AuthService;import io.swagger.v3.oas.annotations.Operation;import io.swagger.v3.oas.annotations.media.Content;import io.swagger.v3.oas.annotations.media.Schema;import io.swagger.v3.oas.annotations.responses.ApiResponse;import io.swagger.v3.oas.annotations.responses.ApiResponses;import io.swagger.v3.oas.annotations.tags.Tag;import jakarta.validation.Valid;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * auth 컨트롤러 * * @author : 오승훈 * @fileName : AuthController * @since : 2026-02-22 */@Tag(name = "인증", description = "회원가입 및 로그인 API")@Slf4j@RestController@RequestMapping("/api/auth")@RequiredArgsConstructorpublic class AuthController {  private final AuthService authService;  /**   * 회원가입   */  @Operation(summary = "회원가입", description = "새로운 사용자를 등록합니다.")  @ApiResponses({      @ApiResponse(responseCode = "200", description = "회원가입 성공",          content = @Content(schema = @Schema(implementation = SignupResponse.class))),      @ApiResponse(responseCode = "400", description = "잘못된 입력값 또는 중복된 로그인 ID")  })  @PostMapping("/signup")  public ResponseEntity<SignupResponse> signup(@Valid @RequestBody SignupRequest request) {    log.info("회원가입 요청 수신 - 로그인 ID: {}", request.getLoginId());    SignupResponse response = authService.signup(request);    return ResponseEntity.ok(response);  }  /**   * 로그인   */  @Operation(summary = "로그인", description = "로그인 ID와 비밀번호로 로그인하여 JWT 토큰을 발급받습니다.")  @ApiResponses({      @ApiResponse(responseCode = "200", description = "로그인 성공",          content = @Content(schema = @Schema(implementation = LoginResponse.class))),      @ApiResponse(responseCode = "401", description = "로그인 ID 또는 비밀번호 불일치")  })  @PostMapping("/login")  public ResponseEntity<LoginResponse> login(@Valid @RequestBody LoginRequest request) {    log.info("로그인 요청 수신 - 로그인 ID: {}", request.getLoginId());    LoginResponse response = authService.login(request);    return ResponseEntity.ok(response);  }}