package com.example.chatbot.user.dto;import lombok.AllArgsConstructor;import lombok.Getter;/** * 로그인 응답 dto * * @author : 오승훈 * @fileName : LoginResponse * @since : 2026-02-22 */@Getter@AllArgsConstructorpublic class LoginResponse {  private String accessToken;  private String tokenType;  private Long userId;  private String loginId;  private String name;  public static LoginResponse of(String token, Long userId, String loginId, String name) {    return new LoginResponse(token, "Bearer", userId, loginId, name);  }}