package com.example.chatbot.user.dto;import jakarta.validation.constraints.NotBlank;import jakarta.validation.constraints.Size;import lombok.AccessLevel;import lombok.Getter;import lombok.NoArgsConstructor;/** * 회원가입 요청 dto * * @author : 오승훈 * @fileName : SignupRequest * @since : 2026-02-22 */@Getter@NoArgsConstructor(access = AccessLevel.PROTECTED)public class SignupRequest {  @NotBlank(message = "로그인 ID는 필수입니다.")  @Size(min = 4, max = 20, message = "로그인 ID는 4~20자 사이여야 합니다.")  private String loginId;  @NotBlank(message = "비밀번호는 필수입니다.")  @Size(min = 8, message = "비밀번호는 최소 8자 이상이어야 합니다.")  private String password;  @NotBlank(message = "이름은 필수입니다.")  @Size(max = 20, message = "이름은 최대 20자까지 가능합니다.")  private String name;}