package com.example.chatbot.user.entity;import com.example.chatbot.common.entity.BaseTime;import jakarta.persistence.*;import lombok.AccessLevel;import lombok.Builder;import lombok.Getter;import lombok.NoArgsConstructor;/** * user 엔티티 * * @author : 오승훈 * @fileName : User * @since : 2026-02-04 */@Entity@Table(name = "users")@Getter@NoArgsConstructor(access = AccessLevel.PROTECTED)public class User extends BaseTime {  @Id  @GeneratedValue(strategy = GenerationType.IDENTITY)  @Column(name = "user_id")  private Long userId;  @Column(name = "login_id", length = 128, nullable = false, unique = true)  private String loginId;  @Column(name = "password", nullable = false)  private String password;  @Column(name = "name", length = 20)  private String name;  @Builder  public User(String loginId, String password, String name) {    this.loginId = loginId;    this.password = password;    this.name = name;  }}