package com.example.chatbot.user.repository;import com.example.chatbot.user.entity.User;import java.util.Optional;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;/** * user 리파지토리 * * @author : 오승훈 * @fileName : UserRepository * @since : 2026-02-22 */@Repositorypublic interface UserRepository extends JpaRepository<User, Long> {  Optional<User> findByLoginId(String loginId);  boolean existsByLoginId(String loginId);}